    const addChannel = () => {
        const channelName = prompt("Please enter the channel name");

        if (channelName) {
            db.collection("rooms").add({
                name: channelName,
            })
        }
    }

    const selectChannel = () => {

    }

    return (
        <Container
            onClick={addChannelOption ? addChannel : selectChannel}
        >
            {Icon && <Icon font-size="small" style={{ padding: 10 }} />}
            {Icon ? (
                <h3>{title}</h3>
            ) : (
                <Channel>
                    <span>#</span> {title}
                </Channel>
            )}
        </Container>
    )
}

