import styled from "styled-components"

export const Container = styled.div`
    flex: 0.3;
    color: white;
    background-color: var(--slack-color);
    border-top: 1px solid #49274b;
    max-width: 260px;
    margin-top: 60px;
`;

export const Header = styled.div`
    display: flex;
    border-bottom: 1px solid #49274b;
    padding: 13px;

    > .MuiSvgIcon-root {
        padding: 8px;
        color: #49274b;
        font-size: 18px;
        background-color: white;
        border-radius: 999px;
        cursor: pointer;
    }
`;

export const Info = styled.div`
    flex: 1;

    > h2 {
        font-size: 15px;
        font-weight: 900;
        margin-bottom: 5px;
    }

    > h3 {
        display: flex;
        font-size: 13px;
        font-weight: 400px;
        align-items: center;
    }
`;
